//[奇遇]50[神秘商店]随机物品库package cn.mxz;public class AgainstGoodsLibraryTemplet {	/** 	 * 唯一ID 	 */	private int idfen;	/** 	 * 物品id 	 */	private String reward;	/** 	 * 道具名称 	 */	private String propNeame;	/** 	 * 物品类型（1图纸 2乙级伙伴魂魄 3甲级魂魄 4甲+魂魄 5道具 6乙级成品装备 7甲级材料） 	 */	private int type;	/** 	 * 品阶 	 */	private int step;	/** 	 * 权重 	 */	private int weight;	/** 	 * 单次兑换获得数量 	 */	private int number;	/** 	 * 兑换所需消耗 	 */	private String consume;	/**	 * 唯一ID	 */	void setIdfen(int idfen) {		this.idfen = idfen;	}	/**	 * 唯一ID	 */	public int getIdfen() {		return this.idfen;	}	/**	 * 物品id	 */	void setReward(String reward) {		this.reward = reward;	}	/**	 * 物品id	 */	public String getReward() {		return this.reward;	}	/**	 * 道具名称	 */	void setPropNeame(String propNeame) {		this.propNeame = propNeame;	}	/**	 * 道具名称	 */	public String getPropNeame() {		return this.propNeame;	}	/**	 * 物品类型（1图纸 2乙级伙伴魂魄 3甲级魂魄 4甲+魂魄 5道具 6乙级成品装备 7甲级材料）	 */	void setType(int type) {		this.type = type;	}	/**	 * 物品类型（1图纸 2乙级伙伴魂魄 3甲级魂魄 4甲+魂魄 5道具 6乙级成品装备 7甲级材料）	 */	public int getType() {		return this.type;	}	/**	 * 品阶	 */	void setStep(int step) {		this.step = step;	}	/**	 * 品阶	 */	public int getStep() {		return this.step;	}	/**	 * 权重	 */	void setWeight(int weight) {		this.weight = weight;	}	/**	 * 权重	 */	public int getWeight() {		return this.weight;	}	/**	 * 单次兑换获得数量	 */	void setNumber(int number) {		this.number = number;	}	/**	 * 单次兑换获得数量	 */	public int getNumber() {		return this.number;	}	/**	 * 兑换所需消耗	 */	void setConsume(String consume) {		this.consume = consume;	}	/**	 * 兑换所需消耗	 */	public String getConsume() {		return this.consume;	}}