package mongo.gen;import com.mongodb.BasicDBObject;import com.mongodb.DBCollection;import com.mongodb.DBCursor;import com.mongodb.DBObject;import java.util.Iterator;import cn.javaplus.exception.UnImplMethodException;public class KeyValueDataDao {	private DBCollection	collection;	public KeyValueDataDao(DBCollection collection) {		this.collection = collection;	}	public void save(KeyValueDataDto u) {		collection.save(u.toDBObject());	}	public void delete(KeyValueDataDto u) {		collection.remove(u.toDBObject());	}	public void delete(String uname) {		collection.remove(key(uname));	}	public KeyValueDataDto get(String uname) {		DBObject o = collection.findOne(key(uname));		if(o == null) {			return null;		}		KeyValueDataDto x = new KeyValueDataDto();		x.fromDBObject(o);		return x;	}	private BasicDBObject key(String uname) {		BasicDBObject o = new BasicDBObject();		o.put("_id", uname);		return o;	}	public KeyValueDataDtoCursor find() {		return new KeyValueDataDtoCursor(collection.find());	}	public long getCount() {		return collection.count();	}	public KeyValueDataDtoCursor findByUname(String uname) {		collection.ensureIndex("uname");		BasicDBObject o = new BasicDBObject("uname", uname);		return new KeyValueDataDtoCursor(collection.find(o));	}
	public KeyValueDataDtoCursor findByData(String data) {				BasicDBObject o = new BasicDBObject("data", data);		return new KeyValueDataDtoCursor(collection.find(o));	}
	public void clear () {		collection.drop();	}	public KeyValueDataDto createDTO() {		return new KeyValueDataDto();	}	public static class KeyValueDataDtoCursor implements Iterator<KeyValueDataDto>, Iterable<KeyValueDataDto>{		private DBCursor	cursor;		public KeyValueDataDtoCursor(DBCursor cursor) {			this.cursor = cursor;		}		public boolean hasNext() {			return cursor.hasNext();		}		public KeyValueDataDto next() {			DBObject next = cursor.next();			KeyValueDataDto dto = new KeyValueDataDto();			dto.fromDBObject(next);			return dto;		}		public int getCount() {			return cursor.count();		}		public void remove() {			throw new UnImplMethodException();		}		public Iterator<KeyValueDataDto> iterator() {			return this;		}	}}