package cn.javaplus.mxzrobot.db;import com.mongodb.BasicDBObject;import com.mongodb.DBCollection;import com.mongodb.DBCursor;import com.mongodb.DBObject;import java.util.Iterator;import cn.javaplus.exception.UnImplMethodException;public class AbilityDao {	private DBCollection	collection;	public AbilityDao(DBCollection collection) {		this.collection = collection;	}	public void save(AbilityDto u) {		collection.save(u.toDBObject());	}	public void delete(AbilityDto u) {		collection.remove(u.toDBObject());	}	public void delete(String key) {		collection.remove(key(key));	}	public AbilityDto get(String key) {		DBObject o = collection.findOne(key(key));		if(o == null) {			return null;		}		AbilityDto x = new AbilityDto();		x.fromDBObject(o);		return x;	}	private BasicDBObject key(String key) {		BasicDBObject o = new BasicDBObject();		o.put("_id", key);		return o;	}	public AbilityDtoCursor find() {		return new AbilityDtoCursor(collection.find());	}	public long getCount() {		return collection.count();	}	public AbilityDtoCursor findByKey(String key) {		collection.ensureIndex("key");		BasicDBObject o = new BasicDBObject("key", key);		return new AbilityDtoCursor(collection.find(o));	}
	public AbilityDtoCursor findByValue(String value) {				BasicDBObject o = new BasicDBObject("value", value);		return new AbilityDtoCursor(collection.find(o));	}
	public void clear () {		collection.drop();	}	public AbilityDto createDTO() {		return new AbilityDto();	}	public static class AbilityDtoCursor implements Iterator<AbilityDto>, Iterable<AbilityDto>{		private DBCursor	cursor;		public AbilityDtoCursor(DBCursor cursor) {			this.cursor = cursor;		}		public boolean hasNext() {			return cursor.hasNext();		}		public AbilityDto next() {			DBObject next = cursor.next();			AbilityDto dto = new AbilityDto();			dto.fromDBObject(next);			return dto;		}		public int getCount() {			return cursor.count();		}		public void remove() {			throw new UnImplMethodException();		}		public Iterator<AbilityDto> iterator() {			return this;		}	}}