package cn.javaplus.mxzrobot.db;import com.mongodb.BasicDBObject;import com.mongodb.DBCollection;import com.mongodb.DBCursor;import com.mongodb.DBObject;import java.util.Iterator;import cn.javaplus.exception.UnImplMethodException;public class AttributeDao {	private DBCollection	collection;	public AttributeDao(DBCollection collection) {		this.collection = collection;	}	public void save(AttributeDto u) {		collection.save(u.toDBObject());	}	public void delete(AttributeDto u) {		collection.remove(u.toDBObject());	}	public void delete(String key) {		collection.remove(key(key));	}	public AttributeDto get(String key) {		DBObject o = collection.findOne(key(key));		if(o == null) {			return null;		}		AttributeDto x = new AttributeDto();		x.fromDBObject(o);		return x;	}	private BasicDBObject key(String key) {		BasicDBObject o = new BasicDBObject();		o.put("_id", key);		return o;	}	public AttributeDtoCursor find() {		return new AttributeDtoCursor(collection.find());	}	public long getCount() {		return collection.count();	}	public AttributeDtoCursor findByKey(String key) {		collection.ensureIndex("key");		BasicDBObject o = new BasicDBObject("key", key);		return new AttributeDtoCursor(collection.find(o));	}
	public AttributeDtoCursor findByValue(String value) {				BasicDBObject o = new BasicDBObject("value", value);		return new AttributeDtoCursor(collection.find(o));	}
	public void clear () {		collection.drop();	}	public AttributeDto createDTO() {		return new AttributeDto();	}	public static class AttributeDtoCursor implements Iterator<AttributeDto>, Iterable<AttributeDto>{		private DBCursor	cursor;		public AttributeDtoCursor(DBCursor cursor) {			this.cursor = cursor;		}		public boolean hasNext() {			return cursor.hasNext();		}		public AttributeDto next() {			DBObject next = cursor.next();			AttributeDto dto = new AttributeDto();			dto.fromDBObject(next);			return dto;		}		public int getCount() {			return cursor.count();		}		public void remove() {			throw new UnImplMethodException();		}		public Iterator<AttributeDto> iterator() {			return this;		}	}}