package cn.javaplus.mxzrobot.db;import com.mongodb.BasicDBObject;import com.mongodb.DBCollection;import com.mongodb.DBCursor;import com.mongodb.DBObject;import java.util.Iterator;import cn.javaplus.exception.UnImplMethodException;public class KeyValueDao {	private DBCollection	collection;	public KeyValueDao(DBCollection collection) {		this.collection = collection;	}	public void save(KeyValueDto u) {		collection.save(u.toDBObject());	}	public void delete(KeyValueDto u) {		collection.remove(u.toDBObject());	}	public void delete(String key) {		collection.remove(key(key));	}	public KeyValueDto get(String key) {		DBObject o = collection.findOne(key(key));		if(o == null) {			return null;		}		KeyValueDto x = new KeyValueDto();		x.fromDBObject(o);		return x;	}	private BasicDBObject key(String key) {		BasicDBObject o = new BasicDBObject();		o.put("_id", key);		return o;	}	public KeyValueDtoCursor find() {		return new KeyValueDtoCursor(collection.find());	}	public long getCount() {		return collection.count();	}	public KeyValueDtoCursor findByKey(String key) {		collection.ensureIndex("key");		BasicDBObject o = new BasicDBObject("key", key);		return new KeyValueDtoCursor(collection.find(o));	}
	public KeyValueDtoCursor findByValue(String value) {				BasicDBObject o = new BasicDBObject("value", value);		return new KeyValueDtoCursor(collection.find(o));	}
	public void clear () {		collection.drop();	}	public KeyValueDto createDTO() {		return new KeyValueDto();	}	public static class KeyValueDtoCursor implements Iterator<KeyValueDto>, Iterable<KeyValueDto>{		private DBCursor	cursor;		public KeyValueDtoCursor(DBCursor cursor) {			this.cursor = cursor;		}		public boolean hasNext() {			return cursor.hasNext();		}		public KeyValueDto next() {			DBObject next = cursor.next();			KeyValueDto dto = new KeyValueDto();			dto.fromDBObject(next);			return dto;		}		public int getCount() {			return cursor.count();		}		public void remove() {			throw new UnImplMethodException();		}		public Iterator<KeyValueDto> iterator() {			return this;		}	}}