//[关卡]宝箱几率package cn.mxz;public class BoxOddsTemplet {	/** 	 * 宝箱id 	 */	private int boxId;	/** 	 * 宝箱名称 	 */	private String boxName;	/** 	 * 宝箱出现的地图ID范围 	 */	private String mapId;	/** 	 * 刷新宝箱时出现权重 	 */	private int boxWeight;	/**	 * 宝箱id	 */	void setBoxId(int boxId) {		this.boxId = boxId;	}	/**	 * 宝箱id	 */	public int getBoxId() {		return this.boxId;	}	/**	 * 宝箱名称	 */	void setBoxName(String boxName) {		this.boxName = boxName;	}	/**	 * 宝箱名称	 */	public String getBoxName() {		return this.boxName;	}	/**	 * 宝箱出现的地图ID范围	 */	void setMapId(String mapId) {		this.mapId = mapId;	}	/**	 * 宝箱出现的地图ID范围	 */	public String getMapId() {		return this.mapId;	}	/**	 * 刷新宝箱时出现权重	 */	void setBoxWeight(int boxWeight) {		this.boxWeight = boxWeight;	}	/**	 * 刷新宝箱时出现权重	 */	public int getBoxWeight() {		return this.boxWeight;	}}