//[神兽]神兽成长package cn.mxz;public class DogzLevelTemplet {	/** 	 * 等级 	 */	private int level;	/** 	 * 升级所需魂力 	 */	private int soulPower;	/** 	 * 每次消耗兽魂数量 	 */	private int soulNumber;	/** 	 * 直接升级的基础概率 	 */	private float upgrade;	/** 	 * 单个兽魂增加的魂力 	 */	private float singleSoulPower;	/** 	 * 所需总兽魂（数值用） 	 */	private int sumSoulNumber;	/** 	 * 身价 	 */	private int social;	/**	 * 等级	 */	void setLevel(int level) {		this.level = level;	}	/**	 * 等级	 */	public int getLevel() {		return this.level;	}	/**	 * 升级所需魂力	 */	void setSoulPower(int soulPower) {		this.soulPower = soulPower;	}	/**	 * 升级所需魂力	 */	public int getSoulPower() {		return this.soulPower;	}	/**	 * 每次消耗兽魂数量	 */	void setSoulNumber(int soulNumber) {		this.soulNumber = soulNumber;	}	/**	 * 每次消耗兽魂数量	 */	public int getSoulNumber() {		return this.soulNumber;	}	/**	 * 直接升级的基础概率	 */	void setUpgrade(float upgrade) {		this.upgrade = upgrade;	}	/**	 * 直接升级的基础概率	 */	public float getUpgrade() {		return this.upgrade;	}	/**	 * 单个兽魂增加的魂力	 */	void setSingleSoulPower(float singleSoulPower) {		this.singleSoulPower = singleSoulPower;	}	/**	 * 单个兽魂增加的魂力	 */	public float getSingleSoulPower() {		return this.singleSoulPower;	}	/**	 * 所需总兽魂（数值用）	 */	void setSumSoulNumber(int sumSoulNumber) {		this.sumSoulNumber = sumSoulNumber;	}	/**	 * 所需总兽魂（数值用）	 */	public int getSumSoulNumber() {		return this.sumSoulNumber;	}	/**	 * 身价	 */	void setSocial(int social) {		this.social = social;	}	/**	 * 身价	 */	public int getSocial() {		return this.social;	}}