//[阵法]替补属性库package cn.mxz;public class FormationBenchTemplet {	/** 	 * 属性id 	 */	private int id;	/** 	 * 位置品阶 	 */	private int spet;	/** 	 * 属性类型（0气血 1物攻 2法攻 3物防 4法防 5速度 6暴击 7闪避 8格挡 9抗暴 10 命中 11破格 12会心） 	 */	private int natureType;	/** 	 * 属性加成百分比 	 */	private float addition;	/** 	 * 权重 	 */	private int weight;	/**	 * 属性id	 */	void setId(int id) {		this.id = id;	}	/**	 * 属性id	 */	public int getId() {		return this.id;	}	/**	 * 位置品阶	 */	void setSpet(int spet) {		this.spet = spet;	}	/**	 * 位置品阶	 */	public int getSpet() {		return this.spet;	}	/**	 * 属性类型（0气血 1物攻 2法攻 3物防 4法防 5速度 6暴击 7闪避 8格挡 9抗暴 10 命中 11破格 12会心）	 */	void setNatureType(int natureType) {		this.natureType = natureType;	}	/**	 * 属性类型（0气血 1物攻 2法攻 3物防 4法防 5速度 6暴击 7闪避 8格挡 9抗暴 10 命中 11破格 12会心）	 */	public int getNatureType() {		return this.natureType;	}	/**	 * 属性加成百分比	 */	void setAddition(float addition) {		this.addition = addition;	}	/**	 * 属性加成百分比	 */	public float getAddition() {		return this.addition;	}	/**	 * 权重	 */	void setWeight(int weight) {		this.weight = weight;	}	/**	 * 权重	 */	public int getWeight() {		return this.weight;	}}