//[神将]神将属性颜色表package cn.mxz;public class GodColourTemplet {	/** 	 * 颜色id 	 */	private int id;	/** 	 * 初始生命 	 */	private int hp;	/** 	 * 初始攻击 	 */	private int attack;	/** 	 * 初始法强 	 */	private int mAttack;	/** 	 * 初始防御 	 */	private int defend;	/** 	 * 初始法抗 	 */	private int mDefend;	/** 	 * 初始速度 	 */	private int speed;	/** 	 * 颜色值 	 */	private String color;	/**	 * 颜色id	 */	void setId(int id) {		this.id = id;	}	/**	 * 颜色id	 */	public int getId() {		return this.id;	}	/**	 * 初始生命	 */	void setHp(int hp) {		this.hp = hp;	}	/**	 * 初始生命	 */	public int getHp() {		return this.hp;	}	/**	 * 初始攻击	 */	void setAttack(int attack) {		this.attack = attack;	}	/**	 * 初始攻击	 */	public int getAttack() {		return this.attack;	}	/**	 * 初始法强	 */	void setMAttack(int mAttack) {		this.mAttack = mAttack;	}	/**	 * 初始法强	 */	public int getMAttack() {		return this.mAttack;	}	/**	 * 初始防御	 */	void setDefend(int defend) {		this.defend = defend;	}	/**	 * 初始防御	 */	public int getDefend() {		return this.defend;	}	/**	 * 初始法抗	 */	void setMDefend(int mDefend) {		this.mDefend = mDefend;	}	/**	 * 初始法抗	 */	public int getMDefend() {		return this.mDefend;	}	/**	 * 初始速度	 */	void setSpeed(int speed) {		this.speed = speed;	}	/**	 * 初始速度	 */	public int getSpeed() {		return this.speed;	}	/**	 * 颜色值	 */	void setColor(String color) {		this.color = color;	}	/**	 * 颜色值	 */	public String getColor() {		return this.color;	}}