//[消息提示]package cn.mxz;public class MessageTemplet {	/** 	 * id 	 */	private int id;	/** 	 * 消息(消息頭:消息顯示的目的地) 	 */	private String content;	/** 	 * 類型(哥哥些, 這列記到填個-1) 	 */	private String type;	/** 	 * 策劃辨別的類型 	 */	private String chtype;	/** 	 * 什麼時候會出現該提示? 	 */	private String explain;	/**	 * id	 */	void setId(int id) {		this.id = id;	}	/**	 * id	 */	public int getId() {		return this.id;	}	/**	 * 消息(消息頭:消息顯示的目的地)	 */	void setContent(String content) {		this.content = content;	}	/**	 * 消息(消息頭:消息顯示的目的地)	 */	public String getContent() {		return this.content;	}	/**	 * 類型(哥哥些, 這列記到填個-1)	 */	void setType(String type) {		this.type = type;	}	/**	 * 類型(哥哥些, 這列記到填個-1)	 */	public String getType() {		return this.type;	}	/**	 * 策劃辨別的類型	 */	void setChtype(String chtype) {		this.chtype = chtype;	}	/**	 * 策劃辨別的類型	 */	public String getChtype() {		return this.chtype;	}	/**	 * 什麼時候會出現該提示?	 */	void setExplain(String explain) {		this.explain = explain;	}	/**	 * 什麼時候會出現該提示?	 */	public String getExplain() {		return this.explain;	}}