//[竞技场]排名奖励package cn.mxz;public class RankRewardTemplet {	/** 	 * id 	 */	private int id;	/** 	 * 排名段开始 	 */	private int step;	/** 	 * 排名段结尾 	 */	private int end;	/** 	 * 所属段位 	 */	private int danLv;	/** 	 * 说明 	 */	private String explain;	/** 	 * 奖励 	 */	private String awards;	/**	 * id	 */	void setId(int id) {		this.id = id;	}	/**	 * id	 */	public int getId() {		return this.id;	}	/**	 * 排名段开始	 */	void setStep(int step) {		this.step = step;	}	/**	 * 排名段开始	 */	public int getStep() {		return this.step;	}	/**	 * 排名段结尾	 */	void setEnd(int end) {		this.end = end;	}	/**	 * 排名段结尾	 */	public int getEnd() {		return this.end;	}	/**	 * 所属段位	 */	void setDanLv(int danLv) {		this.danLv = danLv;	}	/**	 * 所属段位	 */	public int getDanLv() {		return this.danLv;	}	/**	 * 说明	 */	void setExplain(String explain) {		this.explain = explain;	}	/**	 * 说明	 */	public String getExplain() {		return this.explain;	}	/**	 * 奖励	 */	void setAwards(String awards) {		this.awards = awards;	}	/**	 * 奖励	 */	public String getAwards() {		return this.awards;	}}