//[奇遇]机器人难度形象配置package cn.mxz;public class SnatchDifficultyTemplet {	/** 	 * 配置id 	 */	private int id;	/** 	 * 难度配置（品阶） 	 */	private String stepScope;	/** 	 * 战斗力最小值 	 */	private float min;	/** 	 * 战斗力最大值 	 */	private float max;	/**	 * 配置id	 */	void setId(int id) {		this.id = id;	}	/**	 * 配置id	 */	public int getId() {		return this.id;	}	/**	 * 难度配置（品阶）	 */	void setStepScope(String stepScope) {		this.stepScope = stepScope;	}	/**	 * 难度配置（品阶）	 */	public String getStepScope() {		return this.stepScope;	}	/**	 * 战斗力最小值	 */	void setMin(float min) {		this.min = min;	}	/**	 * 战斗力最小值	 */	public float getMin() {		return this.min;	}	/**	 * 战斗力最大值	 */	void setMax(float max) {		this.max = max;	}	/**	 * 战斗力最大值	 */	public float getMax() {		return this.max;	}}