package cn.mxz.script;import static cn.javaplus.util.Util.Random.isHappen;/** 寻仙 findfighter */public class Findfighter {	/** 万里寻一 特殊处理 第n次 必出甲 */	public int getStep(int times) {				if(times == 0) {			return 3;		}				if (times == 1 || times % 10 == 4) {			if (isHappen(0.1f)) {				return 5;			} else {				return 4;			}		}		return -1;	}	public static void main(String[] args) {		for (int i = 0; i < 120; i++) {			int step = ScriptOld.findfighter.getStep(i);			if (step != -1)				System.out.println(i + ":" + step);		}	}}