package db.domain;import java.util.Arrays;public class ActivityScoreRewardImpl implements ActivityScoreReward {	@Key	private String uname;	private String scores;	public ActivityScoreRewardImpl() {	}	public ActivityScoreRewardImpl(ActivityScoreReward c) {		this.uname = c.getUname();		this.scores = c.getScores();	}	@Override	public String tableName() {		return "activity_score_reward";	}	public void setUname(String uname) {		this.uname = uname;	}	public String getUname() {		return this.uname;	}public void addUname(String uname) {		this.uname += uname;}	public void setScores(String scores) {		this.scores = scores;	}	public String getScores() {		return this.scores;	}public void addScores(String scores) {		this.scores += scores;}}