package db.domain;import java.util.Arrays;public class BossScoreSharedImpl implements BossScoreShared {	@Key	private String uanme;	@Key	private String shareId;	private int score;	public BossScoreSharedImpl() {	}	public BossScoreSharedImpl(BossScoreShared c) {		this.uanme = c.getUanme();		this.shareId = c.getShareId();		this.score = c.getScore();	}	@Override	public String tableName() {		return "boss_score_shared";	}	public void setUanme(String uanme) {		this.uanme = uanme;	}	public String getUanme() {		return this.uanme;	}public void addUanme(String uanme) {		this.uanme += uanme;}	public void setShareId(String shareId) {		this.shareId = shareId;	}	public String getShareId() {		return this.shareId;	}public void addShareId(String shareId) {		this.shareId += shareId;}	public void setScore(int score) {		this.score = score;	}	public int getScore() {		return this.score;	}public void addScore(int score) {		this.score += score;}}