package db.domain;import java.util.Arrays;public class ContinuousImpl implements Continuous {	@Key	private String uname;	@Key	private int type;	private String status;	private int lastUpdateDay;	public ContinuousImpl() {	}	public ContinuousImpl(Continuous c) {		this.uname = c.getUname();		this.type = c.getType();		this.status = c.getStatus();		this.lastUpdateDay = c.getLastUpdateDay();	}	@Override	public String tableName() {		return "continuous";	}	public void setUname(String uname) {		this.uname = uname;	}	public String getUname() {		return this.uname;	}public void addUname(String uname) {		this.uname += uname;}	public void setType(int type) {		this.type = type;	}	public int getType() {		return this.type;	}public void addType(int type) {		this.type += type;}	public void setStatus(String status) {		this.status = status;	}	public String getStatus() {		return this.status;	}public void addStatus(String status) {		this.status += status;}	public void setLastUpdateDay(int lastUpdateDay) {		this.lastUpdateDay = lastUpdateDay;	}	public int getLastUpdateDay() {		return this.lastUpdateDay;	}public void addLastUpdateDay(int lastUpdateDay) {		this.lastUpdateDay += lastUpdateDay;}}