package db.domain;public class GemInfo implements 
						UserItem
					{	private String uname;	private int gemId;	private int equipmentId;	private int typeid;	private int exp;	public GemInfo() {	}	public GemInfo(GemInfo c) {		this.uname = c.uname;		this.gemId = c.gemId;		this.equipmentId = c.equipmentId;		this.typeid = c.typeid;		this.exp = c.exp;	}	@Override	public void setUname(String uname) {		this.uname = uname;	}	@Override	public String getUname() {		return this.uname;	}public void addUname(String uname) {		this.uname += uname;}	public void setGemId(int gemId) {		this.gemId = gemId;	}	public int getGemId() {		return this.gemId;	}public void addGemId(int gemId) {		this.gemId += gemId;}	public void setEquipmentId(int equipmentId) {		this.equipmentId = equipmentId;	}	public int getEquipmentId() {		return this.equipmentId;	}public void addEquipmentId(int equipmentId) {		this.equipmentId += equipmentId;}	public void setTypeid(int typeid) {		this.typeid = typeid;	}	public int getTypeid() {		return this.typeid;	}public void addTypeid(int typeid) {		this.typeid += typeid;}	public void setExp(int exp) {		this.exp = exp;	}	public int getExp() {		return this.exp;	}public void addExp(int exp) {		this.exp += exp;}}