package db.domain;import java.util.Arrays;public class GongdePrizeImpl implements GongdePrize {	@Key	private String uname;	private String prize;	public GongdePrizeImpl() {	}	public GongdePrizeImpl(GongdePrize c) {		this.uname = c.getUname();		this.prize = c.getPrize();	}	@Override	public String tableName() {		return "gongde_prize";	}	public void setUname(String uname) {		this.uname = uname;	}	public String getUname() {		return this.uname;	}public void addUname(String uname) {		this.uname += uname;}	public void setPrize(String prize) {		this.prize = prize;	}	public String getPrize() {		return this.prize;	}public void addPrize(String prize) {		this.prize += prize;}}