package db.domain;import java.util.Arrays;public class InviteUsersImpl implements InviteUsers {	@Key	private String uname;	@Key	private String friend;	public InviteUsersImpl() {	}	public InviteUsersImpl(InviteUsers c) {		this.uname = c.getUname();		this.friend = c.getFriend();	}	@Override	public String tableName() {		return "invite_users";	}	public void setUname(String uname) {		this.uname = uname;	}	public String getUname() {		return this.uname;	}public void addUname(String uname) {		this.uname += uname;}	public void setFriend(String friend) {		this.friend = friend;	}	public String getFriend() {		return this.friend;	}public void addFriend(String friend) {		this.friend += friend;}}