package db.domain;import org.bson.types.ObjectId;public class NewFormation implements Domain , 
						UserItem
					{	@Key	private String uname;	private int formationId;	private int level;	private int exp;	private String position;	public NewFormation() {	}	public NewFormation(NewFormation c) {		this.uname = c.uname;		this.formationId = c.formationId;		this.level = c.level;		this.exp = c.exp;		this.position = c.position;	}	@Override	public String tableName() {		return "new_formation";	}	private ObjectId _id;	public ObjectId _id(){		return _id;	}	public void set_id(ObjectId _id) {		this._id = _id;	}	@Override	public void setUname(String uname) {		this.uname = uname;	}	@Override	public String getUname() {		return this.uname;	}public void addUname(String uname) {		this.uname += uname;}	public void setFormationId(int formationId) {		this.formationId = formationId;	}	public int getFormationId() {		return this.formationId;	}public void addFormationId(int formationId) {		this.formationId += formationId;}	public void setLevel(int level) {		this.level = level;	}	public int getLevel() {		return this.level;	}public void addLevel(int level) {		this.level += level;}	public void setExp(int exp) {		this.exp = exp;	}	public int getExp() {		return this.exp;	}public void addExp(int exp) {		this.exp += exp;}	public void setPosition(String position) {		this.position = position;	}	public String getPosition() {		return this.position;	}public void addPosition(String position) {		this.position += position;}}