package db.domain;import java.util.Arrays;import java.util.Date;public class PvpExtraImpl implements PvpExtra {	@Key	private String uname;	private String status;	private Date pvpUpTime;	public static final int V_LEN = 15;	private int [] v = new int[15];	public PvpExtraImpl() {	}	public PvpExtraImpl(PvpExtra c) {		this.uname = c.getUname();		this.status = c.getStatus();		this.pvpUpTime = c.getPvpUpTime();		for (int i = 0; i < v.length; i++) {
			v[i] = c.getV(i);
		}
	}	@Override	public String tableName() {		return "pvp_extra";	}	public void setUname(String uname) {		this.uname = uname;	}	public String getUname() {		return this.uname;	}public void addUname(String uname) {		this.uname += uname;}	public void setStatus(String status) {		this.status = status;	}	public String getStatus() {		return this.status;	}public void addStatus(String status) {		this.status += status;}	public void setPvpUpTime(Date pvpUpTime) {		this.pvpUpTime = pvpUpTime;	}	public Date getPvpUpTime() {		return this.pvpUpTime;	}	public void setV(int index, int value) {		this.v[index] = value;	}	public int getV(int index) {		return this.v[index];	}}