package db.domain;import java.util.Arrays;public class SpiriteImpl implements Spirite {	@Key	private String uname;	@Key	private int typeId;	private int step;	private int count;	public SpiriteImpl() {	}	public SpiriteImpl(Spirite c) {		this.uname = c.getUname();		this.typeId = c.getTypeId();		this.step = c.getStep();		this.count = c.getCount();	}	@Override	public String tableName() {		return "spirite";	}	public void setUname(String uname) {		this.uname = uname;	}	public String getUname() {		return this.uname;	}public void addUname(String uname) {		this.uname += uname;}	public void setTypeId(int typeId) {		this.typeId = typeId;	}	public int getTypeId() {		return this.typeId;	}public void addTypeId(int typeId) {		this.typeId += typeId;}	public void setStep(int step) {		this.step = step;	}	public int getStep() {		return this.step;	}public void addStep(int step) {		this.step += step;}	public void setCount(int count) {		this.count = count;	}	public int getCount() {		return this.count;	}public void addCount(int count) {		this.count += count;}}