package db.domain;import java.util.Arrays;public class UserBaseImpl implements UserBase {	@Key	private String uname;	private String nick;	private int lastLoginTime;	public UserBaseImpl() {	}	public UserBaseImpl(UserBase c) {		this.uname = c.getUname();		this.nick = c.getNick();		this.lastLoginTime = c.getLastLoginTime();	}	@Override	public String tableName() {		return "user_base";	}	public void setUname(String uname) {		this.uname = uname;	}	public String getUname() {		return this.uname;	}public void addUname(String uname) {		this.uname += uname;}	public void setNick(String nick) {		this.nick = nick;	}	public String getNick() {		return this.nick;	}public void addNick(String nick) {		this.nick += nick;}	public void setLastLoginTime(int lastLoginTime) {		this.lastLoginTime = lastLoginTime;	}	public int getLastLoginTime() {		return this.lastLoginTime;	}public void addLastLoginTime(int lastLoginTime) {		this.lastLoginTime += lastLoginTime;}}