package cn.mxz.handler;public interface BagService extends cn.mxz.util.Service{	/**	 * 获得背包详细内容	 *	 * @return 背包所有数据	 */	cn.mxz.protocols.user.god.BagP.BagPro getData();	/**	 * 获得临时背包所有数据	 *	 * @return 临时背包所有数据	 */	cn.mxz.protocols.user.god.BagP.BagPro getTempBagData();	/**	 * 使用指定位置的某个道具	 *	 * @param position 被使用道具的索引位置	 * @return null	 */	cn.mxz.protocols.user.mission.BoxP.BoxPro useProp(int position);	/**	 * 获得某个物品在背包中的数量	 *	 * @param id 物品的ID(对应到Excel的第一列)	 * @return 某个物品在背包中的数量	 */	cn.mxz.protocols.user.PropP.PropPro getCount(int id);	/**	 * 获得一堆物品在背包中的数量	 *	 * @param ids 物品的ID(对应到Excel的第一列) 用逗号连接各个ID	 * @return 某个物品在背包中的数量	 */	cn.mxz.protocols.user.PropP.PropsPro getCounts(String ids);	/**	 * 使用某个道具	 *	 * @param propType 道具类型	 * @param count 使用数量	 * @param fighterId 战士唯一ID(非Excel第一列) 如果无法取得战士ID , 发-1(比如某个草是对全体战士起效的)	 * @return null	 */	cn.mxz.protocols.user.mission.BoxP.BoxPro usePropToFighter(int propType, int count, int fighterId);	/**	 * 获得某个物品在背包中的数量	 *	 * @param id 物品的ID(对应到Excel的第一列)	 * @return 某个物品在背包中的数量	 */	cn.mxz.protocols.user.PropP.PropPro getCountBySpotId(int id);	/**	 * 使用某个道具	 *	 * @param spotId 道具类型	 * @param count 使用数量	 * @param fighterId 战士唯一ID(非Excel第一列) 如果无法取得战士ID , 发-1(比如某个草是对全体战士起效的)	 * @return null	 */	cn.mxz.protocols.user.mission.BoxP.BoxPro usePropBySpotId(int spotId, int count, int fighterId);	/**	 * 从临时背包将物品移动到正式背包	 *	 * @param gridId 背包格子ID	 * 	 */	void moveToBagFromTempBag(int gridId);	/**	 * 使用某个道具	 *	 * @param propType 道具类型	 * @param count 使用数量	 * @return null	 */	cn.mxz.protocols.user.mission.BoxP.BoxPro useProp2(int propType, int count);	/**	 * 获得碎片背包详细内容	 *	 * @return 碎片背包所有数据	 */	cn.mxz.protocols.user.god.BagP.BagPro getPiecesBag();	/**	 * 使用某个道具	 *	 * @param propType 道具类型	 * @param count 使用数量	 * @return null	 */	cn.mxz.protocols.user.mission.BoxP.BoxPro buyAndUseProp2(int propType, int count);	/**	 * 获取某个道具今日剩余使用次数	 *	 * @param propType 道具类型	 * @return 剩余使用次数/今日总共可以使用多少次 例子: 13/20	 */	String getRemainUseTimes(int propType);	/**	 * 获得所有背包数据, 仅仅瞿伟在 夺宝时使用	 *	 * @return 背包所有数据	 */	cn.mxz.protocols.user.god.BagP.BagPro getBagAll();	/**	 * 卖出道具	 *	 * @param propTypesAndcounts 道具id1, 数量1, 道具id2, 数量2,.....	 * 	 */	void sell(String propTypesAndcounts);}