package cn.mxz.handler;public interface ChatService extends cn.mxz.util.Service{	/**	 * 发送消息到世界	 *	 * @param message 消息内容	 * 	 */	void sendToWorld(String message);	/**	 * 发送消息到我自己的联盟	 *	 * @param message 消息内容	 * 	 */	void sendToUnion(String message);	/**	 * 发送消息到某个人	 *	 * @param message 消息内容	 * @param userId 玩家账号	 * 	 */	void sendToUser(String message, String userId);	/**	 * 通知后端打开了世界聊天	 *	 * @param type 0:世界 1:联盟 2:私聊	 * 	 */	void openMessage(int type);}