package cn.mxz.handler;public interface GPSService extends cn.mxz.util.Service{	/**	 * 发送自己的坐标给服务器	 *	 * @param longitude 经度 x 10000	 * @param latitude 纬度 x 10000	 * 	 */	void sendLocation(int longitude, int latitude);	/**	 * 获得附近的人的列表	 *	 * @param page 页码	 * @param countOfPage 每一页有几个人, 最大不能超过100	 * @return 附近的人的列表	 */	cn.mxz.protocols.user.LookAroundP.LookAroundPro getLookAround(int page, int countOfPage);	/**	 * 获得附近的人的列表	 *	 * @param page 页码	 * @param countOfPage 每一页有几个人, 最大不能超过100	 * @return 附近的人的列表	 */	cn.mxz.protocols.user.LookAroundP.LookGirlPro getLookgirl(int page, int countOfPage);	/**	 * 获得附近的人的列表	 *	 * @param page 页码	 * @param countOfPage 每一页有几个人, 最大不能超过100	 * @return 附近的人的列表	 */	cn.mxz.protocols.user.LookAroundP.LookBoyPro getLookboy(int page, int countOfPage);	/**	 * 删除自己位置	 *	 * 	 */	void removeGPS();}