package cn.mxz.handler;public interface InitService extends cn.mxz.util.Service{	/**	 * 接入游戏服务器	 *	 * @param id 用户ID	 * @param token 合作商加密串	 * @param adultState 成人标志 1 成年 2 先固定传一个2 就行了	 * @param mac 客户端mac	 * @param clientType 客户端类型:1 ios;2 android;3 其它 （手游）	 * @param UnixTime 时间戳（手游）	 * @param otherValue 预留值	 * @return 是否是新用户	 */	cn.mxz.protocols.user.UserP.AccessResultPro access(String id, String token, int adultState, String mac, int clientType, int UnixTime, String otherValue);	/**	 * 设置角色昵称	 *	 * @param nick 用户昵称	 * @return null	 */	int setNick(String nick);	/**	 * 确定创建角色	 *	 * @param invitationCode 邀请码	 * @return 是否创建成功	 */	Boolean create(String invitationCode);	/**	 * 设定主神将类型ID	 *	 * @param fighterTypeId 神将类型ID	 * @return null	 */	int setUserType(int fighterTypeId);	/**	 * 确定创建角色	 *	 * @param fighterTypeId 神将类型ID	 * @param invitationCode 邀请码	 * @return 错误Code 如果为0 表示成功	 */	int createUser(int fighterTypeId, String invitationCode);	/**	 * 重置帐号	 *	 * 	 */	void resetUser();	/**	 * 获得一个随机昵称	 *	 * @return 随机的昵称	 */	String getRandomNick();}