package cn.mxz.handler;public interface MarketService extends cn.mxz.util.Service{	/**	 * 我的交易信息列表（"我的交易"）	 *	 * @return 交易信息	 */	cn.mxz.protocols.user.market.MarketP.MyTradePro getMyTrade();	/**	 * 提出交易	 *	 * @param fighterId 所选神将的id	 * @param tradeNeed 交易所选择的条件（字符串的形式）	 * 	 */	void getChooseFighterPro(String fighterId, String tradeNeed);	/**	 * 查找交易信息	 *	 * @param typeId 所要查找神将Id	 * @param page 第几页	 * @return 交易信息	 */	cn.mxz.protocols.user.market.MarketP.TradeFighterMessagePro queryTradeMes(int typeId, int page);	/**	 * 接受交易	 *	 * @param fighterId 神将唯一Id	 * @param uName 神将属于哪个玩家	 * 	 */	void AcceptTrade(int fighterId, String uName);	/**	 * 取消交易	 *	 * @param fighterId 神将唯一Id	 * @param uName 神将属于哪个玩家	 * 	 */	void cancelTrade(int fighterId, String uName);	/**	 * 根据品阶在市场上查找信息	 *	 * @param step 所要查询的品阶	 * @return 交易信息	 */	cn.mxz.protocols.user.market.MarketP.TradeFighterMessagePro queryToStep(int step);}