package cn.mxz.handler;public interface NewFormationService extends cn.mxz.util.Service{	/**	 * 获得主界面信息	 *	 * @return 主界面信息	 */	cn.mxz.protocols.newformation.NewCampP.NewCampPro getData();	/**	 * 查看其它玩家信息	 *	 * @param userId userId 其它玩家id	 * @return 主界面信息	 */	cn.mxz.protocols.newformation.NewCampP.OtherCampPro lookUpOther(String userId);	/**	 * 阵法升级	 *	 * @param source source 源id	 * @param fregment fregment 用逗号分隔的阵法id字符串	 * @return null	 */	Boolean tacticalUp(int source, String fregment);	/**	 * 选择阵法	 *	 * @param id 阵法id	 * 	 */	void setCurrentTacticalId(int id);	/**	 * 刷新替补位	 *	 * @param position 替补位的位置，从100开始	 * @return null	 */	Boolean refreshBackupPos(int position);	/**	 * 设置英雄的位置	 *	 * @param heroId heroId 英雄id	 * @param targetPos targetPos 目标位置	 * @param targetHeroId targetHeroId 目标位置的英雄id，没有则为-1	 * @return null	 */	Boolean change(int heroId, int targetPos, int targetHeroId);	/**	 * 设置英雄的位置	 *	 * @param heroId heroId 英雄id	 * @param targetPos targetPos 目标位置	 * @return null	 */	Boolean move(int heroId, int targetPos);	/**	 * 合成阵法	 *	 * @param targetId targetId 目标阵法id	 * @return 新阵法	 */	cn.mxz.protocols.newformation.NewCampP.TacticalPro compound(int targetId);}