package cn.mxz.handler;public interface PVPFormationService extends cn.mxz.util.Service{	/**	 * 设置为出战阵容(选定某个阵形为默认阵形)	 *	 * @param formationId 待选定的阵形ID	 * 	 */	void selectPVP(int formationId);	/**	 * 拖动某个人从某个位置拖动到另外一个位置, 1.如果positionSrc位置没有人, throw new IllOperationException("指定位置没有人") 2.如果2个位置都有人, 交换两个人的位置	 *	 * @param positionSrc 源位置(从0开始)	 * @param positionDst 目标位置(从0开始)	 * 	 */	void movePVP(int positionSrc, int positionDst);	/**	 * 往指定点添加战士	 *	 * @param fighterTypeId 战士类型ID	 * @param position 添加位置	 * 	 */	void addFighterPVP(int fighterTypeId, int position);	/**	 * 移除指定位置的战士	 *	 * @param position 移除位置	 * 	 */	void removeFighterPVP(int position);}