package cn.mxz.handler;public interface PracticeService extends cn.mxz.util.Service{	/**	 * 关注好友	 *	 * @param friendId 好友ID	 * 	 */	void careFor(String friendId);	/**	 * 求关注	 *	 * @param friendId 好友ID	 * 	 */	void askForCare(String friendId);	/**	 * 获得求关注列表	 *	 * @return null	 */	cn.mxz.protocols.user.PracticeDataP.AskForCarePro getAskForCareList();	/**	 * 获得双休的战斗属性加成	 *	 * @return null	 */	cn.mxz.protocols.user.PracticeDataP.PracticeAdditionPro getAddition();	/**	 * 删除玩家求关注信息	 *	 * @param userId 玩家ID	 * 	 */	void removeCareData(String userId);	/**	 * 获得我的修练场信息	 *	 * @return 我的修练场信息	 */	cn.mxz.protocols.user.PracticeDataP.PracticeDataPro getData();	/**	 * 获得双休日志	 *	 * @return 双休日志	 */	cn.mxz.protocols.user.PracticeDataP.PracticeLogListPro getLog();	/**	 * 查看别人的修练场信息	 *	 * @param userId 别人的帐号	 * @return 别人的修练场信息	 */	cn.mxz.protocols.user.PracticeDataP.PracticeDataPro lookUpOther(String userId);	/**	 * 获得双休邀请列表	 *	 * @param page 当前页码	 * @param countOfPage 每页记录条数	 * @return null	 */	cn.mxz.protocols.user.PracticeDataP.PracticeRequestListPro getRequest(int page, int countOfPage);	/**	 * 邀请和别人双修	 *	 * @param userId 玩家帐号	 * @param mode 修炼模式	 * 	 */	void request(String userId, int mode);	/**	 * 同意和别人双休	 *	 * @param userId 和我双修的那个人的Id	 * 	 */	void accept(String userId);	/**	 * 拒绝和别人双修	 *	 * @param userId 玩家帐号	 * 	 */	void refuse(String userId);	/**	 * 获得双休好友列表	 *	 * @param page 当前页码	 * @param countOfPage 每页记录条数	 * @return null	 */	cn.mxz.protocols.user.PracticeDataP.PracticeFriendListPro getFriends(int page, int countOfPage);	/**	 * 结束和别人双休	 *	 * 	 */	void stopPracticeWithOther();	/**	 * 亲密示爱	 *	 * 	 */	void qmsa();	/**	 * 百年好合	 *	 * 	 */	void bnhh();	/**	 * 比翼双飞	 *	 * 	 */	void bysf();	/**	 * 设置是否自动接受双休	 *	 * @param isAutoAccept 是否自动接受	 * 	 */	void setAutoAccept(Boolean isAutoAccept);}