package cn.mxz.handler;public interface YunyouService extends cn.mxz.util.Service{	/**	 * 获得主界面信息	 *	 * @return 主界面信息	 */	cn.mxz.protocols.yunyou.YunyouP.YunyouSPro getData();	/**	 * 通过唯一id获取一个云游仙人的信息	 *	 * @param id id 云游仙人的唯一id	 * @return 云游仙人的信息	 */	cn.mxz.protocols.yunyou.YunyouP.YunyouPro getById(int id);	/**	 * 限时抢购	 *	 * @param id id 云游仙人的唯一id	 * 	 */	void buy(int id);	/**	 * 指点结束，获取经验	 *	 * @param id index 从0开始的序列号	 * @param isDouble isDouble 是否双倍购买	 * 	 */	void getExp(int id, Boolean isDouble);	/**	 * 指点结束，强制获取双倍经验,如果需要的道具“酒”不足则直接购买	 *	 * @param id index 从0开始的序列号	 * 	 */	void getExpForceDouble(int id);}