package cn.mxz.messagesender;import com.lemon.commons.socket.ISocket;import cn.javaplus.comunication.PacketCompressedByZlib;public class ActivityFriendMessageSenderImpl implements ActivityFriendMessageSender{	/**	 *	 */	public void onGetRequest(ISocket socket, String userId, String nick) {		PacketCompressedByZlib p = new PacketCompressedByZlib();		p.putInt(30010);				p.putString("");		p.putString(userId);		p.putString(nick);		p.send(socket);	}	/**	 *	 */	public void onToBeFriend(ISocket socket, String userId, String nick) {		PacketCompressedByZlib p = new PacketCompressedByZlib();		p.putInt(30011);				p.putString("");		p.putString(userId);		p.putString(nick);		p.send(socket);	}	/**	 *	 */	public void onFriendMessage(ISocket socket, String userId, String nick) {		PacketCompressedByZlib p = new PacketCompressedByZlib();		p.putInt(30012);				p.putString("");		p.putString(userId);		p.putString(nick);		p.send(socket);	}}