package cn.mxz.messagesender;import com.lemon.commons.socket.ISocket;import cn.javaplus.comunication.PacketCompressedByZlib;public class BagMessageSenderImpl implements BagMessageSender{	/**	 *	 */	public void bagUpdate(ISocket socket, cn.mxz.protocols.user.god.BagP.BagPro changes) {		PacketCompressedByZlib p = new PacketCompressedByZlib();		p.putInt(10015);				p.putString("");		p.put(changes);		p.send(socket);	}	/**	 *	 */	public void bagRemoved(ISocket socket, String bagIdsRemoved) {		PacketCompressedByZlib p = new PacketCompressedByZlib();		p.putInt(10016);				p.putString("");		p.putString(bagIdsRemoved);		p.send(socket);	}	/**	 *	 */	public void piecesBagUpdate(ISocket socket, cn.mxz.protocols.user.god.BagP.BagPro changes) {		PacketCompressedByZlib p = new PacketCompressedByZlib();		p.putInt(10017);				p.putString("");		p.put(changes);		p.send(socket);	}	/**	 *	 */	public void piecesBagRemoved(ISocket socket, String bagIdsRemoved) {		PacketCompressedByZlib p = new PacketCompressedByZlib();		p.putInt(10018);				p.putString("");		p.putString(bagIdsRemoved);		p.send(socket);	}}