package cn.mxz.messagesender;import com.lemon.commons.socket.ISocket;import cn.javaplus.comunication.PacketCompressedByZlib;public class BossMessageSenderImpl implements BossMessageSender{	/**	 *	 */	public void onReceiveBoss(ISocket socket, int bossId, int count) {		PacketCompressedByZlib p = new PacketCompressedByZlib();		p.putInt(40023);				p.putString("");		p.putInt(bossId);		p.putInt(count);		p.send(socket);	}}