package cn.mxz.messagesender;import com.lemon.commons.socket.ISocket;import cn.javaplus.comunication.PacketCompressedByZlib;public class ChatMessageSenderImpl implements ChatMessageSender{	/**	 *	 */	public void onMessageWorld(ISocket socket, String message) {		PacketCompressedByZlib p = new PacketCompressedByZlib();		p.putInt(60004);				p.putString("");		p.putString(message);		p.send(socket);	}	/**	 *	 */	public void onMessageUnion(ISocket socket, String message) {		PacketCompressedByZlib p = new PacketCompressedByZlib();		p.putInt(60005);				p.putString("");		p.putString(message);		p.send(socket);	}	/**	 *	 */	public void onMessageUser(ISocket socket, String message) {		PacketCompressedByZlib p = new PacketCompressedByZlib();		p.putInt(60006);				p.putString("");		p.putString(message);		p.send(socket);	}	/**	 *	 */	public void receiveGmMessage(ISocket socket, String title, String body, String mailType, String senderName) {		PacketCompressedByZlib p = new PacketCompressedByZlib();		p.putInt(60007);				p.putString("");		p.putString(title);		p.putString(body);		p.putString(mailType);		p.putString(senderName);		p.send(socket);	}}