package cn.mxz.messagesender;import com.lemon.commons.socket.ISocket;import cn.javaplus.comunication.PacketCompressedByZlib;public class EquipmentMessageSenderImpl implements EquipmentMessageSender{	/**	 *	 */	public void equipmentsUpdate(ISocket socket, cn.mxz.protocols.user.equipment.EquipmentP.EquipmentsPro equipments) {		PacketCompressedByZlib p = new PacketCompressedByZlib();		p.putInt(130020);				p.putString("");		p.put(equipments);		p.send(socket);	}	/**	 *	 */	public void equipmentsRemove(ISocket socket, String equipmentIds) {		PacketCompressedByZlib p = new PacketCompressedByZlib();		p.putInt(130021);				p.putString("");		p.putString(equipmentIds);		p.send(socket);	}}