package cn.mxz.messagesender;import com.lemon.commons.socket.ISocket;import cn.javaplus.comunication.PacketCompressedByZlib;public class FighterMessageSenderImpl implements FighterMessageSender{	/**	 *	 */	public void fightersUpdate(ISocket socket, cn.mxz.protocols.user.god.FighterP.FightersPro fighters) {		PacketCompressedByZlib p = new PacketCompressedByZlib();		p.putInt(140020);				p.putString("");		p.put(fighters);		p.send(socket);	}	/**	 *	 */	public void fightersRemove(ISocket socket, String fighterIds) {		PacketCompressedByZlib p = new PacketCompressedByZlib();		p.putInt(140021);				p.putString("");		p.putString(fighterIds);		p.send(socket);	}	/**	 *	 */	public void capacityChanged(ISocket socket, int capacityOld, int capacityNew) {		PacketCompressedByZlib p = new PacketCompressedByZlib();		p.putInt(140022);				p.putString("");		p.putInt(capacityOld);		p.putInt(capacityNew);		p.send(socket);	}	/**	 *	 */	public void tianMingChanged(ISocket socket, String tianMingAdd, String tianMingRemove) {		PacketCompressedByZlib p = new PacketCompressedByZlib();		p.putInt(140023);				p.putString("");		p.putString(tianMingAdd);		p.putString(tianMingRemove);		p.send(socket);	}}