package cn.mxz.messagesender;import com.lemon.commons.socket.ISocket;import cn.javaplus.comunication.PacketCompressedByZlib;public class FriendMessageSenderImpl implements FriendMessageSender{	/**	 *	 */	public void sendMessage(ISocket socket, String message) {		PacketCompressedByZlib p = new PacketCompressedByZlib();		p.putInt(170016);				p.putString("");		p.putString(message);		p.send(socket);	}	/**	 *	 */	public void onGetRequest(ISocket socket, String userId, String nick) {		PacketCompressedByZlib p = new PacketCompressedByZlib();		p.putInt(170017);				p.putString("");		p.putString(userId);		p.putString(nick);		p.send(socket);	}	/**	 *	 */	public void onToBeFriend(ISocket socket, String userId, String nick) {		PacketCompressedByZlib p = new PacketCompressedByZlib();		p.putInt(170018);				p.putString("");		p.putString(userId);		p.putString(nick);		p.send(socket);	}	/**	 *	 */	public void onFriendMessage(ISocket socket, String userId, String nick) {		PacketCompressedByZlib p = new PacketCompressedByZlib();		p.putInt(170019);				p.putString("");		p.putString(userId);		p.putString(nick);		p.send(socket);	}}