package cn.mxz.messagesender;import com.lemon.commons.socket.ISocket;import cn.javaplus.comunication.PacketCompressedByZlib;public class PracticeMessageSenderImpl implements PracticeMessageSender{	/**	 *	 */	public void sendRequestMessage(ISocket socket, String text) {		PacketCompressedByZlib p = new PacketCompressedByZlib();		p.putInt(300018);				p.putString("");		p.putString(text);		p.send(socket);	}	/**	 *	 */	public void onPracticeStart(ISocket socket, String userId, String nick) {		PacketCompressedByZlib p = new PacketCompressedByZlib();		p.putInt(300019);				p.putString("");		p.putString(userId);		p.putString(nick);		p.send(socket);	}}