package cn.mxz.messagesender;import com.lemon.commons.socket.ISocket;import cn.javaplus.comunication.PacketCompressedByZlib;public class QiYuMessageSenderImpl implements QiYuMessageSender{	/**	 *	 */	public void onButtonUpdate(ISocket socket, String buttons) {		PacketCompressedByZlib p = new PacketCompressedByZlib();		p.putInt(330001);				p.putString("");		p.putString(buttons);		p.send(socket);	}}