package cn.mxz.messagesender;import com.lemon.commons.socket.ISocket;import cn.javaplus.comunication.PacketCompressedByZlib;public class SkillMessageSenderImpl implements SkillMessageSender{	/**	 *	 */	public void skillsUpdate(ISocket socket, cn.mxz.protocols.user.god.FighterP.FighterSkillsPro skills) {		PacketCompressedByZlib p = new PacketCompressedByZlib();		p.putInt(380002);				p.putString("");		p.put(skills);		p.send(socket);	}	/**	 *	 */	public void skillsRemove(ISocket socket, String skillIds) {		PacketCompressedByZlib p = new PacketCompressedByZlib();		p.putInt(380003);				p.putString("");		p.putString(skillIds);		p.send(socket);	}}