package cn.mxz.messagesender;import com.lemon.commons.socket.ISocket;import cn.javaplus.comunication.PacketCompressedByZlib;public class SystemMessageSenderImpl implements SystemMessageSender{	/**	 *	 */	public void error(ISocket socket) {		PacketCompressedByZlib p = new PacketCompressedByZlib();		p.putInt(390003);				p.putString("");		p.send(socket);	}	/**	 *	 */	public void sendMessage(ISocket socket, int code, String formatStr, int messageType) {		PacketCompressedByZlib p = new PacketCompressedByZlib();		p.putInt(390004);				p.putString("");		p.putInt(code);		p.putString(formatStr);		p.putInt(messageType);		p.send(socket);	}	/**	 *	 */	public void kick(ISocket socket) {		PacketCompressedByZlib p = new PacketCompressedByZlib();		p.putInt(390005);				p.putString("");		p.send(socket);	}	/**	 *	 */	public void goldNotEnouph(ISocket socket) {		PacketCompressedByZlib p = new PacketCompressedByZlib();		p.putInt(390006);				p.putString("");		p.send(socket);	}	/**	 *	 */	public void beat(ISocket socket) {		PacketCompressedByZlib p = new PacketCompressedByZlib();		p.putInt(390007);				p.putString("");		p.send(socket);	}}